package erp.testcases;import erp.base.BaseSetup;import erp.common.helpers.ExcelHelper;import erp.common.helpers.PropertiesHelper;import erp.pages.SignInPage;import org.openqa.selenium.WebDriver;import org.testng.Assert;import org.testng.annotations.BeforeClass;import org.testng.annotations.BeforeMethod;import org.testng.annotations.Test;public class SignInTest extends BaseSetup {    private WebDriver driver;    public SignInPage signInPage;    public ExcelHelper excelHelper;    @BeforeClass    public void setUp() {        this.driver = getDriver();        excelHelper = new ExcelHelper();        PropertiesHelper.loadAllFile();        driver.manage().deleteAllCookies();        signInPage = new SignInPage(driver);    }    @BeforeMethod    public void beforeMethod() {        driver.navigate().refresh();    }    @Test(priority = 1)    public void signInWithEmailNull() throws Exception {        signInPage.waitForPageLoaded();        String error = signInPage.verifyEmailnull();        System.out.println(error);        Assert.assertEquals(error, "Email is required.");    }    @Test(priority = 1)    public void sigInWithPasswordNull() throws Exception {        signInPage.waitForPageLoaded();        String error = signInPage.verifyPasswordNUll();        System.out.println(error);        Assert.assertEquals(error, "Password is required.");    }    @Test(priority = 2)    public void siginWrongPassOrEmail() throws Exception {        signInPage.waitForPageLoaded();        String error;        error = signInPage.verifyWrongEmailOrPassword("asdasd@yopmail.com", "Password1234");        Assert.assertEquals(error, "Username or password is incorrect.");        driver.navigate().refresh();        error = signInPage.verifyWrongEmailOrPassword("testdeferp@yopmail.com", "Password12345");        Assert.assertEquals(error, "Username or password is incorrect.");    }    @Test(priority = 2)    public  void signInWithInactiveAccount(){        signInPage.verifyAccountInactive(PropertiesHelper.getValue("inactiveEmail")                ,PropertiesHelper.getValue("inactivePassword")                ,PropertiesHelper.getValue("inactiveContent"));    }    @Test(priority = 4)    public void signInTest() throws Exception {        signInPage.waitForPageLoaded();        excelHelper.setExcelFile("src/test/java/resources/Book1.xlsx", "Sheet1");        Assert.assertTrue(signInPage.verifyLogo(), "Not displayed");        Assert.assertTrue(signInPage.verifyTitle(), "Not same");        Assert.assertTrue(signInPage.verifyUsernameTextBox(), "Not displayed");        Assert.assertTrue(signInPage.verifyPasswordTextBox(), "Not displayed");        System.out.println(PropertiesHelper.getValue("email"));        signInPage.login(PropertiesHelper.getValue("email"), PropertiesHelper.getValue("password"));    }}